CREATE TABLE DEPT (  DEPTNO              NUMERIC(2) NOT NULL,  DNAME               VARCHAR(14),  LOC                 VARCHAR(13),  CONSTRAINT DEPT_PRIMARY_KEY PRIMARY KEY (DEPTNO) );  
CREATE TABLE EMP (  EMPNO NUMERIC(4) NOT NULL,  ENAME VARCHAR(10),  JOB                 VARCHAR(9),  MGR                 NUMERIC(4) CONSTRAINT EMP_MGR_FK REFERENCES EMP (EMPNO),  HIREDATE            DATETIME,  SAL                 NUMERIC(7,2),  COMM                NUMERIC(7,2),  DEPTNO              NUMERIC(2),  CONSTRAINT EMP_DEPTNO_FK FOREIGN KEY (DEPTNO) REFERENCES DEPT (DEPTNO),  CONSTRAINT EMP_EMPNO_PK PRIMARY KEY (EMPNO) );   
CREATE TABLE SALGRADE (  GRADE               NUMERIC,  LOSAL               NUMERIC,  HISAL               NUMERIC );  
CREATE TABLE CUSTOMER (  CUSTID              NUMERIC (6) NOT NULL,  NAME                VARCHAR (45),  ADDRESS             VARCHAR (40),  CITY                VARCHAR (30),  STATE               VARCHAR (2),  ZIP                 VARCHAR (9),  AREA                NUMERIC (3),  PHONE               VARCHAR (9),  REPID               NUMERIC (4),  CREDITLIMIT         NUMERIC (9,2),  COMMENTS            TEXT,  CONSTRAINT CUSTOMER_CUSTID_PK PRIMARY KEY (CUSTID),  CONSTRAINT CUSTOMER_CUSTID_CK CHECK (CUSTID > 0),  CONSTRAINT CUSTOMER_REPID_FK FOREIGN KEY(REPID) REFERENCES EMP(EMPNO)  ); 
CREATE TABLE ORD  (  ORDID               NUMERIC (4) NOT NULL,  ORDERDATE           DATETIME,  COMMPLAN            VARCHAR (1),  CUSTID              NUMERIC (6),  SHIPDATE            DATETIME,  TOTAL               NUMERIC (8,2) CONSTRAINT ORD_TOTAL_CK CHECK (TOTAL >= 0),  CONSTRAINT ORD_CUSTID_FK FOREIGN KEY (CUSTID) REFERENCES CUSTOMER (CUSTID),  CONSTRAINT ORD_ORDID_PK PRIMARY KEY (ORDID) );  
CREATE TABLE PRODUCT (  PRODID              NUMERIC (6) CONSTRAINT PRODUCT_PRODID_PK PRIMARY KEY,  DESCRIP             VARCHAR (30) );  
CREATE TABLE PRICE (  PRODID              NUMERIC (6) NOT NULL,  STARTDATE           DATETIME    NOT NULL,  ENDDATE             DATETIME,  STDPRICE            NUMERIC (8,2),  MINPRICE            NUMERIC (8,2),  CONSTRAINT PRICE_PRODID_STARTDATE_PK PRIMARY KEY(PRODID, STARTDATE),  CONSTRAINT PRICE_PRODID_FK FOREIGN KEY(PRODID) REFERENCES PRODUCT(PRODID) );  
CREATE TABLE ITEM  (  ORDID               NUMERIC (4) NOT NULL,  ITEMID              NUMERIC (4) NOT NULL,  PRODID              NUMERIC (6),  ACTUALPRICE         NUMERIC (8,2),  QTY                 NUMERIC (8),  ITEMTOT             NUMERIC (8,2),  CONSTRAINT ITEM_ORDID_FK FOREIGN KEY (ORDID) REFERENCES ORD (ORDID),  CONSTRAINT ITEM_ORDID_ITEMID_PK PRIMARY KEY (ORDID,ITEMID),  CONSTRAINT ITEM_PRODID_FK FOREIGN KEY(PRODID) REFERENCES PRODUCT(PRODID) ); 



















